mapper.select:
SELECT image_id FROM img_album_image
WHERE album_id = #{albumId}
ORDER BY sort_order ASC
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE img_album SET image_count = CASE WHEN image_count > 0 THEN image_count - 1 ELSE 0 END,
    updated_at = datetime('now')
WHERE id = #{albumId}
